DROP TABLE IF EXISTS cms_options;
CREATE TABLE `cms_options` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` text CHARACTER SET cp1251 NOT NULL,  `caption` text CHARACTER SET cp1251 NOT NULL,  `sort` int(11) NOT NULL,  `parent` int(11) NOT NULL,  `folder` int(11) NOT NULL,  `opt_block` text NOT NULL,  `opt_sort` text NOT NULL,  `opt_by` text NOT NULL,  `opt_count` int(11) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;
DROP TABLE IF EXISTS journal;
CREATE TABLE `journal` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` text NOT NULL,  `caption` text NOT NULL,  `sort` int(11) NOT NULL,  `parent` int(11) NOT NULL,  `folder` int(11) NOT NULL,  `user_id` text NOT NULL,  `block_name` text NOT NULL,  `item_id` int(11) NOT NULL,  `action` text NOT NULL,  `data_after` longtext NOT NULL,  `data_before` longtext NOT NULL,  `date_time` datetime NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=cp1251;
DROP TABLE IF EXISTS modules;
CREATE TABLE `modules` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` text NOT NULL,  `caption` text NOT NULL,  `sort` int(11) NOT NULL,  `parent` int(11) NOT NULL,  `folder` int(11) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=15 DEFAULT CHARSET=cp1251;
DROP TABLE IF EXISTS options;
CREATE TABLE `options` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` text NOT NULL,  `caption` text NOT NULL,  `sort` int(11) NOT NULL,  `parent` int(11) NOT NULL,  `folder` int(11) NOT NULL,  `hvalue` text NOT NULL,  `options_class` text NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=23 DEFAULT CHARSET=cp1251;
DROP TABLE IF EXISTS options_class;
CREATE TABLE `options_class` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` text NOT NULL,  `caption` text NOT NULL,  `sort` int(11) NOT NULL,  `parent` int(11) NOT NULL,  `folder` int(11) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=cp1251;
DROP TABLE IF EXISTS pages;
CREATE TABLE `pages` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` text NOT NULL,  `caption` text NOT NULL,  `sort` int(11) NOT NULL,  `parent` int(11) NOT NULL,  `folder` int(11) NOT NULL,  `keywords` text NOT NULL,  `template` int(11) NOT NULL,  `detail_text` text NOT NULL,  `in_menu` smallint(6) NOT NULL,  `human_url` smallint(6) NOT NULL,  `not_visible` smallint(6) NOT NULL,  `modules` text NOT NULL,  `not_in_bread` smallint(6) NOT NULL,  `caption2` text NOT NULL,  `description` text NOT NULL,  `redirect` text NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=36 DEFAULT CHARSET=cp1251;
DROP TABLE IF EXISTS system_cache;
CREATE TABLE `system_cache` (  `id` varchar(255) NOT NULL,  `value` text NOT NULL,  `time` int(11) NOT NULL,  `module` text NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=cp1251;
DROP TABLE IF EXISTS system_data_block;
CREATE TABLE `system_data_block` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` tinytext CHARACTER SET cp1251 NOT NULL,  `bgroup` int(11) NOT NULL,  `caption` text CHARACTER SET cp1251 NOT NULL,  `sort` int(11) NOT NULL,  `fav` tinyint(1) NOT NULL,  `hide_folders` tinyint(1) NOT NULL,  `hide_folders2` tinyint(1) NOT NULL,  `hide_code` tinyint(1) NOT NULL,  PRIMARY KEY (`id`),  UNIQUE KEY `name` (`name`(255))) ENGINE=MyISAM AUTO_INCREMENT=42 DEFAULT CHARSET=latin1;
DROP TABLE IF EXISTS system_data_block_fields;
CREATE TABLE `system_data_block_fields` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `data_block` int(11) NOT NULL,  `name` tinytext CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL,  `caption` tinytext NOT NULL,  `type` int(11) NOT NULL,  `sort` int(11) NOT NULL,  `required` tinyint(4) NOT NULL,  `multiple` tinyint(4) NOT NULL,  `dgroup` int(11) NOT NULL,  `owner_type` tinyint(4) NOT NULL,  `add_values` text NOT NULL,  `not_list` tinyint(4) NOT NULL,  `admin_only` tinyint(4) NOT NULL,  `meta` tinyint(4) NOT NULL,  `not_element` tinyint(1) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=157 DEFAULT CHARSET=cp1251;
DROP TABLE IF EXISTS system_data_block_group;
CREATE TABLE `system_data_block_group` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `caption` tinytext NOT NULL,  `sort` int(11) NOT NULL,  `name` text NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=17 DEFAULT CHARSET=cp1251;
DROP TABLE IF EXISTS system_data_block_types;
CREATE TABLE `system_data_block_types` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` tinytext NOT NULL,  `caption` tinytext NOT NULL,  `basetype` tinytext NOT NULL,  `sort` int(11) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=17 DEFAULT CHARSET=cp1251;
DROP TABLE IF EXISTS templates;
CREATE TABLE `templates` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` text NOT NULL,  `caption` text NOT NULL,  `sort` int(11) NOT NULL,  `parent` int(11) NOT NULL,  `folder` int(11) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=cp1251;
DROP TABLE IF EXISTS users;
CREATE TABLE `users` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` text NOT NULL,  `caption` text NOT NULL,  `sort` int(11) NOT NULL,  `parent` int(11) NOT NULL,  `folder` int(11) NOT NULL,  `login` text NOT NULL,  `email` text NOT NULL,  `block_control` smallint(6) NOT NULL,  `pass` text NOT NULL,  `uid` tinytext NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=cp1251;
